import React, {useEffect, useState} from "react";
import LoginForm from "./Component/LoginForm";
import AddElement from "./Component/AddElement";
import ListElements from "./Component/ListElements";

function App() {
    useEffect(() => {
        const [elements, setElements] = useState();
        const body = new URLSearchParams({
            username: 'Francis',
            password: 'password',
        })

        const headers = new Headers({
            'Content-Type':'application/x-www-form-urlencoded',
            'Authorization': `Basic ${btoa('Francis:password')}`
        })

        fetch('http://localhost:2345', {
            method: 'POST',
            body: body,
            headers: headers,
            mode: 'cors',
            credentials: 'include'
        })
            .then(res => res.json())
            .then(data => {
                setElements(data)
                console.log(data)
            })
    }, [])

    const [elements, setElements] = useState([
          {
              id: 2,
              title:'Premier article',
              content:'Lorem ipsum dolor sit amet, consectetur adipiscing elit. In rutrum tortor varius, congue lacus non, fringilla tellus. Phasellus nec tincidunt sem. Etiam est ante, condimentum eget justo vitae, mattis ultricies nisi. Maecenas placerat non ipsum eu volutpat. Duis consectetur est urna, facilisis dictum purus scelerisque et. Duis laoreet interdum dapibus. Nullam congue euismod dolor, malesuada hendrerit tortor dictum sit amet. Etiam elementum eget odio ut faucibus. Sed scelerisque tristique nisi in sollicitudin. Sed eros nisi, suscipit et nibh a, placerat iaculis augue. Donec quis eleifend quam, eu accumsan quam. Vestibulum nec posuere urna. Fusce accumsan mollis feugiat. Maecenas id rhoncus nulla. Curabitur at sem sagittis, facilisis leo at, commodo orci.',
              complete: false
          },
          {
              id: 3,
              title:'Second article',
              content:'Sed posuere sapien et rhoncus tempus. Nullam eget lacus non elit suscipit volutpat. Integer fermentum risus id est lobortis ultricies. Integer pellentesque, risus quis aliquam pharetra, ex libero tempus ante, sed pretium nunc lorem quis mi. Sed pulvinar aliquet ante et sagittis. Pellentesque sed sodales ipsum, rutrum laoreet elit. Fusce bibendum lobortis est quis varius. Quisque pharetra bibendum sapien eget luctus. Sed ac elit sem. Curabitur sit amet mattis elit, nec pharetra lectus. Phasellus pharetra in neque id accumsan. Sed sem sem, iaculis sed quam sit amet, tempor venenatis mauris. Sed sollicitudin sem nec tellus vulputate efficitur. Donec euismod, diam sit amet mattis pulvinar, velit enim accumsan nulla, in fringilla lacus orci sed ipsum.',
              complete: false
          }
      ]
    );

    return (
        <>
            <LoginForm />
            <AddElement setElements={setElements}/>
            <h2>Tous les blogs</h2>
            <ListElements elements={elements} setElements={setElements}/>
        </>
    );
}

export default App;
